const {
    statusBarHeight,
    navigationBarHeight,
    screenWidth
} = swan.getSystemInfoSync();

// menuRect无法使用时替代的常量
const menuRectWidth = 97;

Component({

    properties: {
        backgroundColor: {
            type: String,
            value: '#fff'
        }
    },

    data: {
        statusBarHeight,
        navigationBarHeight,
        safeWidth: screenWidth - menuRectWidth
    }

});
